/*global NUM_DAMAGE_TEXT_SPRITES, LARGE_RED_FONT, SCALE_FACTOR, TILE_SIZE, FONT_NAME*/
/*global GREEN_TARGET_BOX_FRAME, RED_SELECT_BOX_FRAME, PURPLE_SELECT_BOX_FRAME, RED_TARGET_BOX_FRAME, X_FRAME*/
/*global FACTION*/
